  type: z.enum(['product', 'service']),
  productId: z.string().optional(),
  name: z.string().min(1, "Name is required"),
  description: z.string().optional(),
  quantity: z.number().min(1, "Quantity must be at least 1"),
  unitPrice: z.number().min(0, "Price must be positive"),
  total: z.number(),
  hasGst: z.boolean().default(false),
  gstAmount: z.number().default(0),
  hasWarranty: z.boolean().default(false),
  warrantyCards: z.array(z.object({
    url: z.string(),
    filename: z.string(),
    uploadedAt: z.string(),
  })).optional().default([]),
});

const invoiceFormSchema = z.object({
  items: z.array(invoiceItemSchema).min(1, "Add at least one item"),
  couponCode: z.string().optional(),
  notes: z.string().optional(),
  terms: z.string().optional(),
});

type InvoiceFormValues = z.infer<typeof invoiceFormSchema>;

interface InvoiceGenerationDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  serviceVisit: any;
}

export function InvoiceGenerationDialog({ open, onOpenChange, serviceVisit }: InvoiceGenerationDialogProps) {
  const { toast } = useToast();
  const [couponValidation, setCouponValidation] = useState<any>(null);
  const [calculatedTotals, setCalculatedTotals] = useState({
    subtotal: 0,
    discount: 0,
    taxAmount: 0,
    total: 0,
  });
  const [initialItemsSet, setInitialItemsSet] = useState(false);

  const form = useForm<InvoiceFormValues>({
    resolver: zodResolver(invoiceFormSchema),
    defaultValues: {
      items: [{
        type: 'service' as const,
        name: 'Service Charge',
        quantity: 1,
        unitPrice: 0,
        total: 0,
        hasGst: false,
        gstAmount: 0,
        hasWarranty: false,
        warrantyCards: [],
      }],
      couponCode: '',
      notes: '',
      terms: 'Payment due within 30 days',
    },
  });

  const { data: products = [] } = useQuery<any[]>({
    queryKey: ['/api/products'],
  });

  const { data: suggestedProductsData, isLoading: loadingSuggestedProducts } = useQuery<{ products: any[] }>({
    queryKey: ['/api/service-visits', serviceVisit?._id, 'suggested-products'],
    enabled: open && !!serviceVisit?._id,
  });

  const items = form.watch('items');
  const couponCode = form.watch('couponCode');

  useEffect(() => {
    if (!open) {
      setInitialItemsSet(false);
      setCouponValidation(null);
      return;
    }

    if (initialItemsSet || loadingSuggestedProducts) {
      return;
    }

    console.log('\n========================================');
    console.log('üìã INVOICE DIALOG - Processing Products');
    console.log('========================================');
    console.log('Service Visit ID:', serviceVisit?._id);
    console.log('Service Visit Vehicle:', serviceVisit?.vehicleReg);
    console.log('Loading Suggested Products?', loadingSuggestedProducts);
    console.log('Suggested Products Data:', suggestedProductsData);
    console.log('Service Visit PartsUsed:', serviceVisit?.partsUsed);
    console.log('Service Visit PartsUsed Count:', serviceVisit?.partsUsed?.length || 0);

    // Build a map of productId -> fresh product data from suggested products
    const freshProductMap = new Map();
    console.log('\nüó∫Ô∏è Building fresh product map from suggested products...');
    suggestedProductsData?.products?.forEach((product: any, index: number) => {
      console.log(`  Product ${index + 1}:`, {
        productId: product.productId,
        name: product.name,
        price: product.price,
        warranty: product.warranty,
      });
      freshProductMap.set(product.productId, {
        name: product.name,
        price: product.price || 0,
        warranty: product.warranty,
      });
    });
    console.log('Fresh product map size:', freshProductMap.size);

    // Create partsUsedItems with fresh prices from the map
    console.log('\nüîß Processing PartsUsed items...');
    const partsUsedItems = serviceVisit?.partsUsed?.map((part: any, index: number) => {
      const productId = part.productId?._id || part.productId;
      const freshData = freshProductMap.get(productId?.toString());

      console.log(`  Part ${index + 1}:`, {
        rawPart: part,
        productId: productId,
        productIdString: productId?.toString(),
        freshData: freshData,
        hasInMap: freshProductMap.has(productId?.toString()),
      });

      const item = {
        type: 'product' as const,
        productId: productId,
        name: freshData?.name || part.productId?.name || 'Product',
        quantity: part.quantity || 1,
        unitPrice: freshData?.price || part.price || 0,
        total: (part.quantity || 1) * (freshData?.price || part.price || 0),
        hasGst: false,
        gstAmount: 0,
        hasWarranty: !!(freshData?.warranty || part.productId?.warranty),
        warrantyCards: [],
      };

      console.log(`    ‚Üí Created item:`, item);
      return item;
    }) || [];

    console.log('Total PartsUsed items created:', partsUsedItems.length);

    // Create suggested items for products not already in partsUsed
    const usedProductIds = new Set(partsUsedItems.map((item: any) => item.productId?.toString()));
    console.log('\nüí° Processing Suggested Products (excluding partsUsed)...');
    console.log('Used Product IDs:', Array.from(usedProductIds));

    const uniqueSuggestedItems = suggestedProductsData?.products
      ?.filter((product: any) => {
        const isUsed = usedProductIds.has(product.productId);
        console.log(`  Product "${product.name}" (${product.productId}):`, isUsed ? 'SKIP (already in partsUsed)' : 'INCLUDE');
        return !isUsed;
      })
      .map((product: any) => ({
        type: 'product' as const,
        productId: product.productId,
        name: product.name,
        quantity: 1,
        unitPrice: product.price || 0,
        total: product.price || 0,
        hasGst: false,
        gstAmount: 0,
        hasWarranty: !!product.warranty,
        warrantyCards: [],
      })) || [];

    console.log('Total Unique Suggested items created:', uniqueSuggestedItems.length);
    console.log('Unique Suggested Items:', uniqueSuggestedItems);

    console.log('\nüìä SUMMARY:');
    console.log('  PartsUsed Items:', partsUsedItems.length);
    console.log('  Unique Suggested Items:', uniqueSuggestedItems.length);
    console.log('  Total Items:', partsUsedItems.length + uniqueSuggestedItems.length);

    const allItems = [...partsUsedItems, ...uniqueSuggestedItems];

    if (allItems.length > 0) {
      console.log('\n‚úÖ Setting', allItems.length, 'items in form');
      console.log('All Items:', allItems);
      form.setValue('items', allItems);
      setInitialItemsSet(true);
    } else {
      console.log('\n‚ö†Ô∏è No items to set, marking as initialized');
      setInitialItemsSet(true);
    }
    console.log('========================================\n');
  }, [open, serviceVisit, suggestedProductsData, initialItemsSet, loadingSuggestedProducts, form]);

  useEffect(() => {
    const subtotal = items.reduce((sum, item) => sum + (item.total || 0), 0);
    const discount = couponValidation?.coupon?.discountAmount || 0;
    const total = subtotal - discount;

    setCalculatedTotals({
      subtotal,
      discount,
      taxAmount: 0,
      total,
    });
  }, [items, couponValidation]);

  const validateCouponMutation = useMutation({
    mutationFn: async (code: string) => {
      const response = await apiRequest('POST', '/api/coupons/validate', {
        code,
        customerId: serviceVisit.customerId._id,
        purchaseAmount: calculatedTotals.subtotal,
      });
      return response.json();
    },
    onSuccess: (data: any) => {
      setCouponValidation(data);
      toast({ title: "Coupon applied successfully", description: `Discount: ‚Çπ${data.coupon.discountAmount}` });
    },
    onError: (error: any) => {
      setCouponValidation(null);
      toast({ title: "Invalid coupon", description: error.message, variant: "destructive" });
    },
  });

  const createInvoiceMutation = useMutation({
    mutationFn: async (data: InvoiceFormValues) => {
      const response = await apiRequest('POST', '/api/invoices/from-service-visit', {
        serviceVisitId: serviceVisit._id,
        ...data,
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/invoices'] });
      queryClient.invalidateQueries({ queryKey: ['/api/service-visits'] });
      toast({ title: "Invoice created successfully", description: "Invoice sent for approval" });
      onOpenChange(false);
      form.reset();
    },
    onError: () => {
      toast({ title: "Failed to create invoice", variant: "destructive" });
    },
  });

  const addItem = () => {
    const currentItems = form.getValues('items');
    form.setValue('items', [
      ...currentItems,
      {
        type: 'product' as const,
        name: '',
        quantity: 1,
        unitPrice: 0,
        total: 0,
        hasGst: false,
        gstAmount: 0,
        hasWarranty: false,
        warrantyCards: [],
      },
    ]);
  };

  const removeItem = (index: number) => {
    const currentItems = form.getValues('items');
    form.setValue('items', currentItems.filter((_, i) => i !== index));
    setTimeout(() => recalculateTotals(), 0);
  };

  const recalculateTotals = () => {
    const currentItems = form.getValues('items');
    const subtotal = currentItems.reduce((sum, item) => sum + (item.total || 0), 0);
    const discount = couponValidation?.coupon?.discountAmount || 0;
    const total = subtotal - discount;

    setCalculatedTotals({
      subtotal,
      discount,
      taxAmount: 0,
      total,
    });
  };

  const updateItemTotal = (index: number) => {
    const items = form.getValues('items');
    const item = items[index];

    const total = item.unitPrice * item.quantity;

    if (item.hasGst) {
      const gstAmount = total * (18 / 118);
      form.setValue(`items.${index}.gstAmount`, gstAmount, { shouldDirty: true, shouldTouch: true, shouldValidate: true });
      form.setValue(`items.${index}.total`, total, { shouldDirty: true, shouldTouch: true, shouldValidate: true });
    } else {
      form.setValue(`items.${index}.gstAmount`, 0, { shouldDirty: true, shouldTouch: true, shouldValidate: true });
      form.setValue(`items.${index}.total`, total, { shouldDirty: true, shouldTouch: true, shouldValidate: true });
    }

    recalculateTotals();
  };

  const toggleGst = (index: number) => {
    const items = form.getValues('items');
    const newHasGst = !items[index].hasGst;
    form.setValue(`items.${index}.hasGst`, newHasGst);
    updateItemTotal(index);
  };

  const applyCoupon = () => {
    const code = form.getValues('couponCode');
    if (code) {
      validateCouponMutation.mutate(code);
    }
  };

  const onSubmit = (data: InvoiceFormValues) => {
    createInvoiceMutation.mutate(data);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-[95vw] sm:max-w-[90vw] lg:max-w-7xl max-h-[95vh] overflow-y-auto" data-testid="dialog-generate-invoice">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Receipt className="h-5 w-5" />
            Generate Invoice
          </DialogTitle>
          <DialogDescription>
            Create invoice for service visit - {serviceVisit?.vehicleReg || 'N/A'}
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <Card>
              <CardContent className="pt-6">
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <h3 className="text-lg font-semibold">Items & Services</h3>
                    <Button type="button" variant="outline" size="sm" onClick={addItem} data-testid="button-add-item">
                      <Plus className="h-4 w-4 mr-1" />
                      Add Item
                    </Button>
                  </div>

                  <div className="hidden md:block border rounded-lg overflow-x-auto">
                    <Table>
                      <TableHeader>
                        <TableRow>
                          <TableHead className="min-w-[120px]">Type</TableHead>
                          <TableHead className="min-w-[200px]">Name</TableHead>
                          <TableHead className="min-w-[80px]">Qty</TableHead>
                          <TableHead className="min-w-[120px]">Unit Price</TableHead>
                          <TableHead className="min-w-[60px]">GST</TableHead>
                          <TableHead className="min-w-[100px]">Total</TableHead>
                          <TableHead className="min-w-[80px]">Warranty</TableHead>
                          <TableHead className="min-w-[60px]"></TableHead>
                        </TableRow>
                      </TableHeader>
                      <TableBody>
                        {items.map((item, index) => (
                          <TableRow key={index}>
                            <TableCell>
                              <FormField
                                control={form.control}
                                name={`items.${index}.type`}
                                render={({ field }) => (
                                  <FormItem>
                                    <Select value={field.value} onValueChange={field.onChange}>
                                      <SelectTrigger className="w-[120px]" data-testid={`select-item-type-${index}`}>
                                        <SelectValue />
                                      </SelectTrigger>
                                      <SelectContent>
                                        <SelectItem value="product">Product</SelectItem>
                                        <SelectItem value="service">Service</SelectItem>
                                      </SelectContent>
                                    </Select>
                                  </FormItem>
                                )}
                              />
                            </TableCell>
                            <TableCell>
                              <FormField
                                control={form.control}
                                name={`items.${index}.name`}
                                render={({ field }) => (
                                  <FormItem>
                                    {item.type === 'product' ? (
                                      <Select
                                        value={form.watch(`items.${index}.productId`) || ''}
                                        onValueChange={(productId) => {
                                          const selectedProduct = products.find((p: any) => p._id === productId);
                                          if (selectedProduct) {
                                            field.onChange(selectedProduct.name);
                                            form.setValue(`items.${index}.productId`, selectedProduct._id);
                                            form.setValue(`items.${index}.unitPrice`, selectedProduct.sellingPrice);
                                            updateItemTotal(index);
                                          }
                                        }}
                                      >
                                        <SelectTrigger data-testid={`select-product-${index}`}>
                                          <SelectValue placeholder="Select product from inventory">
                                            {field.value || 'Select product from inventory'}
                                          </SelectValue>
                                        </SelectTrigger>
                                        <SelectContent>
                                          {products.filter((p: any) => p.stockQty > 0).map((product: any) => (
                                            <SelectItem key={product._id} value={product._id}>
                                              {product.name} - ‚Çπ{product.sellingPrice} ({product.stockQty} in stock)
                                            </SelectItem>
                                          ))}
                                          {products.filter((p: any) => p.stockQty > 0).length === 0 && (
                                            <div className="p-2 text-sm text-muted-foreground text-center">
                                              No products in stock
                                            </div>
                                          )}
                                        </SelectContent>
                                      </Select>
                                    ) : (
                                      <Input {...field} placeholder="Service name" data-testid={`input-item-name-${index}`} />
                                    )}
                                  </FormItem>
                                )}
                              />
                            </TableCell>
                            <TableCell>
                              <FormField
                                control={form.control}
                                name={`items.${index}.quantity`}
                                render={({ field }) => (
                                  <FormItem>
                                    <Input
                                      type="number"
                                      {...field}
                                      onChange={(e) => {
                                        field.onChange(parseFloat(e.target.value) || 0);
                                        updateItemTotal(index);
                                      }}
                                      className="w-20"
                                      data-testid={`input-item-quantity-${index}`}
                                    />
                                  </FormItem>
                                )}
                              />
                            </TableCell>
                            <TableCell>
                              <FormField
                                control={form.control}
                                name={`items.${index}.unitPrice`}
                                render={({ field }) => (
                                  <FormItem>
                                    <Input
                                      type="number"
                                      {...field}
                                      onChange={(e) => {
                                        field.onChange(parseFloat(e.target.value) || 0);
                                        updateItemTotal(index);
                                      }}
                                      className="w-24"
                                      data-testid={`input-item-price-${index}`}
                                    />
                                  </FormItem>
                                )}
                              />
                            </TableCell>
                            <TableCell>
                              <FormField
                                control={form.control}
                                name={`items.${index}.hasGst`}
                                render={({ field }) => (
                                  <FormItem>
                                    <input
                                      type="checkbox"
                                      checked={field.value}
                                      onChange={() => toggleGst(index)}
                                      className="h-4 w-4"
                                      data-testid={`checkbox-gst-${index}`}
                                    />
                                  </FormItem>
                                )}
                              />
                            </TableCell>
                            <TableCell>‚Çπ{item.total.toLocaleString()}</TableCell>
                            <TableCell>
                              <FormField
                                control={form.control}
                                name={`items.${index}.hasWarranty`}
                                render={({ field }) => (
                                  <FormItem>
                                    <input
                                      type="checkbox"
                                      checked={field.value}
                                      onChange={field.onChange}
                                      className="h-4 w-4"
                                      data-testid={`checkbox-warranty-${index}`}
                                    />
                                  </FormItem>
                                )}
                              />
                            </TableCell>
                            <TableCell>
                              {items.length > 1 && (
                                <Button
                                  type="button"
                                  variant="ghost"
                                  size="sm"
                                  onClick={() => removeItem(index)}
                                  data-testid={`button-remove-item-${index}`}
                                >
                                  <Minus className="h-4 w-4" />
                                </Button>
                              )}
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </div>

                  <div className="md:hidden space-y-3">
                    {items.map((item, index) => (
                      <Card key={index} className="p-4 space-y-3">
                        <div className="flex items-center justify-between">
                          <h4 className="font-semibold text-sm">Item {index + 1}</h4>
                          {items.length > 1 && (
                            <Button
                              type="button"
                              variant="ghost"
                              size="sm"
                              onClick={() => removeItem(index)}
                              data-testid={`button-remove-item-${index}`}
                            >
                              <Minus className="h-4 w-4" />
                            </Button>
                          )}
                        </div>

                        <FormField
                          control={form.control}
                          name={`items.${index}.type`}
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Type</FormLabel>
                              <Select value={field.value} onValueChange={field.onChange}>
                                <SelectTrigger data-testid={`select-item-type-${index}`}>
                                  <SelectValue />
                                </SelectTrigger>
                                <SelectContent>
                                  <SelectItem value="product">Product</SelectItem>
                                  <SelectItem value="service">Service</SelectItem>
                                </SelectContent>
                              </Select>
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name={`items.${index}.name`}
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Name</FormLabel>
                              {item.type === 'product' ? (
                                <Select
                                  value={form.watch(`items.${index}.productId`) || ''}
                                  onValueChange={(productId) => {
                                    const selectedProduct = products.find((p: any) => p._id === productId);
                                    if (selectedProduct) {
                                      field.onChange(selectedProduct.name);
                                      form.setValue(`items.${index}.productId`, selectedProduct._id);
                                      form.setValue(`items.${index}.unitPrice`, selectedProduct.sellingPrice);
                                      updateItemTotal(index);
                                    }
                                  }}
                                >
                                  <SelectTrigger data-testid={`select-product-${index}`}>
                                    <SelectValue placeholder="Select product">
                                      {field.value || 'Select product'}
                                    </SelectValue>
                                  </SelectTrigger>
                                  <SelectContent>
                                    {products.filter((p: any) => p.stockQty > 0).map((product: any) => (
                                      <SelectItem key={product._id} value={product._id}>
                                        {product.name} - ‚Çπ{product.sellingPrice}
                                      </SelectItem>
                                    ))}
                                  </SelectContent>
                                </Select>
                              ) : (
                                <Input {...field} placeholder="Service name" data-testid={`input-item-name-${index}`} />
                              )}
                            </FormItem>
                          )}
                        />

                        <div className="grid grid-cols-2 gap-3">
                          <FormField
                            control={form.control}
                            name={`items.${index}.quantity`}
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Qty</FormLabel>
                                <Input
                                  type="number"
                                  {...field}
                                  onChange={(e) => {
                                    field.onChange(parseFloat(e.target.value) || 0);
                                    updateItemTotal(index);
                                  }}
                                  data-testid={`input-item-quantity-${index}`}
                                />
                              </FormItem>
                            )}
                          />
                          <FormField
                            control={form.control}
                            name={`items.${index}.unitPrice`}
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Unit Price</FormLabel>
                                <Input
                                  type="number"
                                  {...field}
                                  onChange={(e) => {
                                    field.onChange(parseFloat(e.target.value) || 0);
                                    updateItemTotal(index);
                                  }}
                                  data-testid={`input-item-price-${index}`}
                                />
                              </FormItem>
                            )}
                          />
                        </div>

                        <div className="flex items-center justify-between">
                          <div className="flex items-center gap-4">
                            <FormField
                              control={form.control}
                              name={`items.${index}.hasGst`}
                              render={({ field }) => (
                                <FormItem className="flex items-center gap-2">
                                  <input
                                    type="checkbox"
                                    checked={field.value}
                                    onChange={() => toggleGst(index)}
                                    className="h-4 w-4"
                                    data-testid={`checkbox-gst-${index}`}
                                  />
                                  <FormLabel className="!mt-0">GST</FormLabel>
                                </FormItem>
                              )}
                            />
                            <FormField
                              control={form.control}
                              name={`items.${index}.hasWarranty`}
                              render={({ field }) => (
                                <FormItem className="flex items-center gap-2">
                                  <input
                                    type="checkbox"
                                    checked={field.value}
                                    onChange={field.onChange}
                                    className="h-4 w-4"
                                    data-testid={`checkbox-warranty-${index}`}
                                  />
                                  <FormLabel className="!mt-0">Warranty</FormLabel>
                                </FormItem>
                              )}
                            />
                          </div>
                          <div className="text-right">
                            <FormLabel className="text-xs text-muted-foreground">Total</FormLabel>
                            <p className="font-semibold">‚Çπ{item.total.toLocaleString()}</p>
                          </div>
                        </div>
                      </Card>
                    ))}
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="pt-6">
                <div className="space-y-4">
                  <h3 className="text-lg font-semibold">Discount</h3>

                  <div className="flex flex-col sm:flex-row gap-2">
                    <FormField
                      control={form.control}
                      name="couponCode"
                      render={({ field }) => (
                        <FormItem className="flex-1">
                          <FormLabel>Coupon Code</FormLabel>
                          <FormControl>
                            <Input {...field} placeholder="Enter coupon code" data-testid="input-coupon-code" />
                          </FormControl>
                        </FormItem>
                      )}
                    />
                    <div className="sm:self-end">
                      <Button
                        type="button"
                        variant="outline"
                        onClick={applyCoupon}
                        disabled={!couponCode || validateCouponMutation.isPending}
                        data-testid="button-apply-coupon"
                        className="w-full sm:w-auto"
                      >
                        <Tag className="h-4 w-4 mr-1" />
                        Apply
                      </Button>
                    </div>
                  </div>

                  {couponValidation && (
                    <Badge variant="default" data-testid="badge-coupon-applied">
                      Coupon Applied: {couponValidation.coupon.discountType === 'percentage'
                        ? `${couponValidation.coupon.discountValue}% off`
                        : `‚Çπ${couponValidation.coupon.discountValue} off`}
                    </Badge>
                  )}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="pt-6">
                <div className="space-y-3">
                  <h3 className="text-lg font-semibold flex items-center gap-2">
                    <Calculator className="h-5 w-5" />
                    Invoice Summary
                  </h3>

                  <div className="bg-muted p-4 rounded-lg space-y-3">
                    <div className="flex justify-between text-base">
                      <span>Subtotal:</span>
                      <span className="font-semibold" data-testid="text-subtotal">‚Çπ{calculatedTotals.subtotal.toLocaleString()}</span>
                    </div>

                    {calculatedTotals.discount > 0 && (
                      <div className="flex justify-between text-green-600">
                        <span>Discount:</span>
                        <span className="font-semibold" data-testid="text-discount">-‚Çπ{calculatedTotals.discount.toLocaleString()}</span>
                      </div>
                    )}

                    <div className="border-t border-border pt-3 mt-3">
                      <div className="flex justify-between text-xl font-bold">
                        <span>Grand Total:</span>
                        <span data-testid="text-grand-total">‚Çπ{calculatedTotals.total.toLocaleString()}</span>
                      </div>
                    </div>
                  </div>

                  <p className="text-sm text-muted-foreground">
                    Note: GST is calculated per item. Check the GST box for items that include GST in their price.
                  </p>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="pt-6">
                <div className="space-y-4">
                  <FormField
                    control={form.control}
                    name="notes"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Notes</FormLabel>
                        <FormControl>
                          <Textarea {...field} placeholder="Additional notes..." data-testid="textarea-notes" />
                        </FormControl>
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="terms"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Terms & Conditions</FormLabel>
                        <FormControl>
                          <Textarea {...field} placeholder="Payment terms and conditions..." data-testid="textarea-terms" />
                        </FormControl>
                      </FormItem>
                    )}
                  />
                </div>
              </CardContent>
            </Card>

            <DialogFooter className="flex-col sm:flex-row gap-2">
              <Button
                type="button"
                variant="outline"
                onClick={() => onOpenChange(false)}
                data-testid="button-cancel-invoice"
                className="w-full sm:w-auto"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={createInvoiceMutation.isPending}
                data-testid="button-create-invoice"
                className="w-full sm:w-auto"
              >
                {createInvoiceMutation.isPending ? "Creating..." : "Create Invoice"}
              </Button>
            </DialogFooter>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/pages/UserManagement.tsx.content:
import { useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { useToast } from '@/hooks/use-toast';
import { UserPlus, Trash2, Edit, Shield, X, FileText } from 'lucide-react';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Switch } from '@/components/ui/switch';
import { queryClient } from '@/lib/queryClient';
import { Link } from 'wouter';
import { ImageCropDialog } from '@/components/ImageCropDialog';
import { useAuth } from '@/lib/auth';

interface User {
  _id: string;
  name: string;
  email: string;
  mobileNumber?: string;
  role: string;
  isActive: boolean;
}

const roles = ['Admin', 'Manager', 'Inventory Manager', 'Sales Executive', 'HR Manager', 'Service Staff'];

export default function UserManagement() {
  const { toast } = useToast();
  const { user: currentUser } = useAuth();
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [isCropDialogOpen, setIsCropDialogOpen] = useState(false);
  const [imageToCrop, setImageToCrop] = useState<string>("");

  // Form states for creating user
  const [newUserName, setNewUserName] = useState('');
  const [newUserEmail, setNewUserEmail] = useState('');
  const [newUserMobile, setNewUserMobile] = useState('');
  const [newUserPassword, setNewUserPassword] = useState('');
  const [newUserRole, setNewUserRole] = useState('Service Staff');

  // Form states for editing user
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    role: "",
    department: "",
    salary: "",
    joiningDate: "",
    panNumber: "",
    aadharNumber: "",
    photo: "",
    documents: [] as string[],
    isActive: true,
  });

  const handleFileUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (!files) return;

    const filePromises = Array.from(files).map(file => {
      return new Promise<string>((resolve, reject) => {
        const reader = new FileReader();
        reader.onload = () => {
          const base64 = reader.result as string;
          resolve(base64);
        };
        reader.onerror = reject;
        reader.readAsDataURL(file);
      });
    });

    try {
      const base64Files = await Promise.all(filePromises);
      setFormData({ ...formData, documents: [...formData.documents, ...base64Files] });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to upload files",
        variant: "destructive",
      });
    }
  };

  const removeDocument = (index: number) => {
    setFormData({
      ...formData,
      documents: formData.documents.filter((_, i) => i !== index)
    });
  };

  const handlePhotoUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = () => {
      setImageToCrop(reader.result as string);
      setIsCropDialogOpen(true);
    };
    reader.onerror = () => {
      toast({
        title: "Error",
        description: "Failed to upload photo",
        variant: "destructive",
      });
    };
    reader.readAsDataURL(file);
  };

  const handleCropComplete = (croppedImage: string) => {
    setFormData({ ...formData, photo: croppedImage });
  };

  const { data: users = [], isLoading } = useQuery<User[]>({
    queryKey: ['/api/users'],
  });

  const createUserMutation = useMutation({
    mutationFn: async (data: { name: string; email: string; mobileNumber: string; password: string; role: string }) => {
      const response = await fetch('/api/users', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify(data),
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to create user');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/users'] });
      setIsCreateDialogOpen(false);
      setNewUserName('');
      setNewUserEmail('');
      setNewUserMobile('');
      setNewUserPassword('');
      setNewUserRole('Service Staff');
      toast({
        title: 'User created',
        description: 'The user has been created successfully.',
      });
    },
    onError: (error: Error) => {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const updateUserMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      const response = await fetch(`/api/users/${id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify(data),
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to update user');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/users'] });
      setIsEditDialogOpen(false);
      setSelectedUser(null);
      toast({
        title: 'User updated',
        description: 'The user has been updated successfully.',
      });
    },
    onError: (error: Error) => {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const deleteUserMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await fetch(`/api/users/${id}`, {
        method: 'DELETE',
        credentials: 'include',
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to delete user');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/users'] });
      toast({
        title: 'User deleted',
        description: 'The user has been deleted successfully.',
      });
    },
    onError: (error: Error) => {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const handleCreateUser = (e: React.FormEvent) => {
    e.preventDefault();
    createUserMutation.mutate({
      name: newUserName,
      email: newUserEmail,
      mobileNumber: newUserMobile,
      password: newUserPassword,
      role: newUserRole,
    });
  };

  const handleEditUser = (e: React.FormEvent) => {
    e.preventDefault();
    if (selectedUser) {
      updateUserMutation.mutate({
        id: selectedUser._id,
        data: {
          name: formData.name,
          mobileNumber: formData.phone,
          role: formData.role,
          isActive: formData.isActive,
        },
      });
    }
  };

  const handleDeleteUser = (id: string) => {
    if (confirm('Are you sure you want to delete this user?')) {
      deleteUserMutation.mutate(id);
    }
  };

  const openEditDialog = (user: User) => {
    setSelectedUser(user);
    setFormData({
      name: user.name,
      email: user.email,
      phone: user.mobileNumber || '',
      role: user.role,
      department: "",
      salary: "",
      joiningDate: "",
      panNumber: "",
      aadharNumber: "",
      photo: "",
      documents: [],
      isActive: user.isActive,
    });
    setIsEditDialogOpen(true);
  };

  const getRoleBadgeColor = (role: string) => {
    switch (role) {
      case 'Admin':
        return 'bg-red-500';
      case 'Manager':
        return 'bg-yellow-500';
      case 'Inventory Manager':
        return 'bg-blue-500';
      case 'Sales Executive':
        return 'bg-green-500';
      case 'HR Manager':
        return 'bg-purple-500';
      case 'Service Staff':
        return 'bg-orange-500';
      default:
        return 'bg-gray-500';
    }
  };

  return (
    <div className="container mx-auto p-3 md:p-6">
      <div className="flex flex-col md:flex-row md:justify-between md:items-center gap-4 mb-6">
        <div>
          <h1 className="text-2xl md:text-3xl font-bold flex items-center gap-2">
            <Shield className="h-6 w-6 md:h-8 md:w-8" />
            User Management
          </h1>
          <p className="text-sm md:text-base text-muted-foreground mt-2">
            Manage user accounts and permissions. All employees have user accounts.
          </p>
        </div>
        <Link href="/employees" className="w-full md:w-auto">
          <Button data-testid="button-create-user" className="w-full md:w-auto">
            <UserPlus className="h-4 w-4 mr-2" />
            Add Employee / User
          </Button>
        </Link>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Users</CardTitle>
          <CardDescription>A list of all users in the system</CardDescription>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <p>Loading users...</p>
          ) : (
            <div className="space-y-4">
              {users.map((user) => (
                <div
                  key={user._id}
                  className="flex items-center justify-between p-4 border rounded-lg hover:bg-muted/50"
                >
                  <div className="flex-1">
                    <div className="flex items-center gap-3">
                      <h3 className="font-semibold">{user.name}</h3>
                      <Badge className={getRoleBadgeColor(user.role)}>{user.role}</Badge>
                      {!user.isActive && <Badge variant="outline">Inactive</Badge>}
                    </div>
                    <p className="text-sm text-muted-foreground mt-1">{user.email}</p>
                    {user.mobileNumber && (
                      <p className="text-sm text-muted-foreground">üì± {user.mobileNumber}</p>
                    )}
                  </div>
                  <div className="flex items-center gap-2">
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => openEditDialog(user)}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => handleDeleteUser(user._id)}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Edit User</DialogTitle>
            <DialogDescription>
              Update user information and permissions.
            </DialogDescription>
          </DialogHeader>
          <form onSubmit={handleEditUser} className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="edit-name">Name *</Label>
                <Input
                  id="edit-name"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  required
                  data-testid="input-edit-name"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="edit-email">Email *</Label>
                <Input
                  id="edit-email"
                  type="email"
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                  required
                  data-testid="input-edit-email"
                />
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="edit-photo">User Photo</Label>
              <Input
                id="edit-photo"
                type="file"
                accept="image/*"
                onChange={handlePhotoUpload}
                data-testid="input-edit-photo"
              />
              {formData.photo && (
                <div className="flex items-center gap-4 mt-2">
                  <img 
                    src={formData.photo} 
                    alt="User preview" 
                    className="h-20 w-20 rounded-full object-cover border-2"
                  />
                  <Button
                    type="button"
                    variant="outline"
                    size="sm"
                    onClick={() => setFormData({ ...formData, photo: "" })}
                    data-testid="button-remove-photo"
                  >
                    Remove Photo
                  </Button>
                </div>
              )}
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="edit-phone">Phone *</Label>
                <Input
                  id="edit-phone"
                  value={formData.phone}
                  onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                  required
                  placeholder="9619523254"
                  data-testid="input-edit-phone"
                />
                <p className="text-xs text-muted-foreground">
                  10-digit mobile number for WhatsApp OTP login
                </p>
              </div>
              <div className="space-y-2">
                <Label htmlFor="edit-role">Role *</Label>
                <Select
                  value={formData.role}
                  onValueChange={(value) => setFormData({ ...formData, role: value })}
                  required
                >
                  <SelectTrigger data-testid="select-edit-role">
                    <SelectValue placeholder="Select role" />
                  </SelectTrigger>
                  <SelectContent>
                    {roles.map((role) => (
                      <SelectItem key={role} value={role}>
                        {role}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>

            {formData.role !== 'Admin' && (
              <>
                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="edit-department">Department</Label>
                    <Input
                      id="edit-department"
                      value={formData.department}
                      onChange={(e) => setFormData({ ...formData, department: e.target.value })}
                      placeholder="e.g., Service, Sales"
                      data-testid="input-edit-department"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="edit-salary">Salary</Label>
                    <Input
                      id="edit-salary"
                      type="number"
                      step="0.01"
                      value={formData.salary}
                      onChange={(e) => setFormData({ ...formData, salary: e.target.value })}
                      data-testid="input-edit-salary"
                    />
                  </div>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="edit-joiningDate">Joining Date</Label>
                  <Input
                    id="edit-joiningDate"
                    type="date"
                    value={formData.joiningDate}
                    onChange={(e) => setFormData({ ...formData, joiningDate: e.target.value })}
                    data-testid="input-edit-joiningdate"
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="edit-panNumber">PAN Number</Label>
                    <Input
                      id="edit-panNumber"
                      value={formData.panNumber}
                      onChange={(e) => setFormData({ ...formData, panNumber: e.target.value })}
                      placeholder="ABCDE1234F"
                      data-testid="input-edit-pan"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="edit-aadharNumber">Aadhar Number</Label>
                    <Input
                      id="edit-aadharNumber"
                      value={formData.aadharNumber}
                      onChange={(e) => setFormData({ ...formData, aadharNumber: e.target.value })}
                      placeholder="1234 5678 9012"
                      data-testid="input-edit-aadhar"
                    />
                  </div>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="edit-documents">Upload Documents (PDF)</Label>
                  <Input
                    id="edit-documents"
                    type="file"
                    accept=".pdf"
                    multiple
                    onChange={handleFileUpload}
                    data-testid="input-edit-documents"
                  />
                  {formData.documents.length > 0 && (
                    <div className="space-y-2 mt-2">
                      <p className="text-sm text-muted-foreground">Uploaded files ({formData.documents.length}):</p>
                      <div className="space-y-1">
                        {formData.documents.map((doc, index) => (
                          <div key={index} className="flex items-center gap-2 p-2 bg-muted rounded-md">
                            <FileText className="h-4 w-4 text-muted-foreground" />
                            <span className="text-sm flex-1">Document {index + 1}</span>
                            <Button
                              type="button"
                              variant="ghost"
                              size="sm"
                              onClick={() => removeDocument(index)}
                              data-testid={`button-remove-document-${index}`}
                            >
                              <X className="h-4 w-4" />
                            </Button>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              </>
            )}

            <div className="space-y-2">
              <div className="flex items-center space-x-2">
                <Switch
                  id="is-active"
                  checked={formData.isActive}
                  onCheckedChange={(checked) => setFormData({ ...formData, isActive: checked })}
                  disabled={
                    selectedUser?.role === 'Admin' && 
                    currentUser?.role !== 'Admin'
                  }
                  data-testid="switch-active"
                />
                <Label htmlFor="is-active">Active</Label>
              </div>
              {selectedUser?.role === 'Admin' && currentUser?.role !== 'Admin' && (
                <p className="text-xs text-muted-foreground">
                  Only Admins can toggle the active status of Admin users
                </p>
              )}
            </div>

            <div className="flex justify-end gap-2 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => setIsEditDialogOpen(false)}
                data-testid="button-cancel"
              >
                Cancel
              </Button>
              <Button type="submit" disabled={updateUserMutation.isPending} data-testid="button-update">
                {updateUserMutation.isPending ? 'Updating...' : 'Update User'}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>

      <ImageCropDialog
        open={isCropDialogOpen}
        onOpenChange={setIsCropDialogOpen}
        imageSrc={imageToCrop}
        onCropComplete={handleCropComplete}
      />
    </div>
  );
}

client/src/components/ui/menubar.tsx.content:
"use client"

import * as React from "react"
import * as MenubarPrimitive from "@radix-ui/react-menubar"
import { Check, ChevronRight, Circle } from "lucide-react"

import { cn } from "@/lib/utils"

function MenubarMenu({
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.Menu>) {
  return <MenubarPrimitive.Menu {...props} />
}

function MenubarGroup({
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.Group>) {
  return <MenubarPrimitive.Group {...props} />
}

function MenubarPortal({
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.Portal>) {
  return <MenubarPrimitive.Portal {...props} />
}

function MenubarRadioGroup({
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.RadioGroup>) {
  return <MenubarPrimitive.RadioGroup {...props} />
}

function MenubarSub({
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.Sub>) {
  return <MenubarPrimitive.Sub data-slot="menubar-sub" {...props} />
}

const Menubar = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.Root>
>(({ className, ...props }, ref) => (
  <MenubarPrimitive.Root
    ref={ref}
    className={cn(
      "flex h-10 items-center space-x-1 rounded-md border bg-background p-1",
      className
    )}
    {...props}
  />
))
Menubar.displayName = MenubarPrimitive.Root.displayName

const MenubarTrigger = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.Trigger>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.Trigger>
>(({ className, ...props }, ref) => (
  <MenubarPrimitive.Trigger
    ref={ref}
    className={cn(
      "flex cursor-default select-none items-center rounded-sm px-3 py-1.5 text-sm font-medium outline-none focus:bg-accent focus:text-accent-foreground data-[state=open]:bg-accent data-[state=open]:text-accent-foreground",
      className
    )}
    {...props}
  />
))
MenubarTrigger.displayName = MenubarPrimitive.Trigger.displayName

const MenubarSubTrigger = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.SubTrigger>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.SubTrigger> & {
    inset?: boolean
  }
>(({ className, inset, children, ...props }, ref) => (
  <MenubarPrimitive.SubTrigger
    ref={ref}
    className={cn(
      "flex cursor-default select-none items-center rounded-sm px-2 py-1.5 text-sm outline-none focus:bg-accent focus:text-accent-foreground data-[state=open]:bg-accent data-[state=open]:text-accent-foreground",
      inset && "pl-8",
      className
    )}
    {...props}
  >
    {children}
    <ChevronRight className="ml-auto h-4 w-4" />
  </MenubarPrimitive.SubTrigger>
))
MenubarSubTrigger.displayName = MenubarPrimitive.SubTrigger.displayName

const MenubarSubContent = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.SubContent>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.SubContent>
>(({ className, ...props }, ref) => (
  <MenubarPrimitive.SubContent
