import { useState } from 'react';
import { useLocation } from 'wouter';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Store } from 'lucide-react';
import { cn } from '@/lib/utils';
import logoImage from '@assets/image_1760164042662.png';
import bgImage from '@assets/bg_1761112040031.jpg';

interface ShopOption {
  id: string;
  name: string;
  location: string;
}

const shops: ShopOption[] = [
  {
    id: 'beed',
    name: 'Shop A',
    location: 'Beed',
  },
  {
    id: 'ahilyanagar',
    name: 'Shop B',
    location: 'Chhatrapati Sambhaji Nagar',
  },
];

export default function ShopSelection() {
  const [selectedShop, setSelectedShop] = useState<string>('');
  const [, setLocation] = useLocation();

  const handleContinue = () => {
    if (selectedShop) {
      localStorage.setItem('selectedShop', selectedShop);
      setLocation('/select-role');
    }
  };

  return (
    <div 
      className="min-h-screen flex items-center justify-center p-4 relative"
      style={{
        backgroundImage: `url(${bgImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat'
      }}
    >
      <div className="absolute inset-0 bg-black/30" />
      <div className="w-full max-w-4xl relative z-10">
        <Card className="bg-white/95 dark:bg-gray-900/95 backdrop-blur-sm mb-6">
          <CardContent className="text-center py-6">
            <div className="flex items-center justify-center gap-3 mb-3">
              <img src={logoImage} alt="Mauli Car World Logo" className="h-14 w-auto" />
              <h1 className="text-3xl font-bold" data-testid="text-title">Mauli Car World</h1>
            </div>
            <p className="text-base text-muted-foreground" data-testid="text-subtitle">
              Select your shop location to continue
            </p>
          </CardContent>
        </Card>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
          {shops.map((shop) => (
            <Card
              key={shop.id}
              className={cn(
                'cursor-pointer transition-all hover:shadow-md bg-white dark:bg-gray-900',
                selectedShop === shop.id && 'ring-2 ring-primary'
              )}
              onClick={() => setSelectedShop(shop.id)}
              data-testid={`card-shop-${shop.id}`}
            >
              <CardContent className="flex items-start gap-4 p-6">
                <div className={cn(
                  'p-3 rounded-lg',
                  selectedShop === shop.id ? 'bg-primary text-primary-foreground' : 'bg-muted'
                )}>
                  <Store className="h-6 w-6" />
                </div>
                <div className="flex-1">
                  <h3 className="font-semibold text-lg mb-1" data-testid={`text-shop-${shop.id}`}>
                    {shop.name}
                  </h3>
                  <p className="text-sm text-muted-foreground" data-testid={`text-location-${shop.id}`}>
                    {shop.location}
                  </p>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        <div className="flex justify-center">
          <Button
            onClick={handleContinue}
            disabled={!selectedShop}
            className="px-8 disabled:opacity-100"
            size="default"
            data-testid="button-continue"
          >
            Continue to Role Selection
          </Button>
        </div>
      </div>
    </div>
  );
}

server/models/SupportTicket.ts.content:
import mongoose from 'mongoose';

const supportTicketSchema = new mongoose.Schema({
  ticketNumber: { 
    type: String, 
    required: true, 
    unique: true 
  },
  customerId: { 
    type: mongoose.Schema.Types.ObjectId, 
    ref: 'RegistrationCustomer', 
    required: true 
  },
  vehicleId: { 
    type: mongoose.Schema.Types.ObjectId, 
    ref: 'RegistrationVehicle' 
  },
  vehicleReg: { type: String },
  subject: { 
    type: String, 
    required: true 
  },
  description: { 
    type: String, 
    required: true 
  },
  category: {
    type: String,
    enum: ['service_quality', 'product_issue', 'billing', 'parts_warranty', 'general'],
    default: 'general'
  },
  priority: {
    type: String,
    enum: ['low', 'medium', 'high', 'urgent'],
    default: 'medium'
  },
  status: {
    type: String,
    enum: ['pending', 'in_progress', 'resolved', 'closed'],
    default: 'pending'
  },
  assignedTo: { 
    type: mongoose.Schema.Types.ObjectId, 
    ref: 'User' 
  },
  createdBy: { 
    type: mongoose.Schema.Types.ObjectId, 
    ref: 'User', 
    required: true 
  },
  resolution: { type: String },
  resolvedAt: { type: Date },
  closedAt: { type: Date },
  whatsappFollowUp: {
    sent: { type: Boolean, default: false },
    sentAt: { type: Date },
    message: { type: String }
  },
  feedbackSent: {
    sent: { type: Boolean, default: false },
    sentAt: { type: Date }
  },
  feedbackId: { 
    type: mongoose.Schema.Types.ObjectId, 
    ref: 'Feedback' 
  },
  notes: [
    {
      note: { type: String, required: true },
      addedBy: { type: mongoose.Schema.Types.ObjectId, ref: 'User' },
      addedAt: { type: Date, default: Date.now }
    }
  ]
}, { timestamps: true });

supportTicketSchema.index({ customerId: 1, createdAt: -1 });
supportTicketSchema.index({ vehicleReg: 1 });
supportTicketSchema.index({ status: 1 });

export const SupportTicket = mongoose.models.SupportTicket || mongoose.model('SupportTicket', supportTicketSchema);

server/scripts/assign-tickets-to-service.content:
import mongoose from 'mongoose';
import { SupportTicket } from '../models/SupportTicket';
import { User } from '../models/User';

const MONGODB_URI = process.env.MONGODB_URI;

if (!MONGODB_URI) {
  console.error('‚ùå MONGODB_URI environment variable is not set');
  process.exit(1);
}

async function assignTicketsToServiceStaff() {
  try {
    console.log('üîå Connecting to MongoDB...');
    await mongoose.connect(MONGODB_URI);
    console.log('‚úÖ Connected to MongoDB');

    // Find all Service Staff users
    const serviceStaffUsers = await User.find({ role: 'Service Staff', isActive: true });
    console.log(`\nüìã Found ${serviceStaffUsers.length} Service Staff users:`);
    serviceStaffUsers.forEach(user => {
      console.log(`  - ${user.name} (${user.email}) - ID: ${user._id}`);
    });

    if (serviceStaffUsers.length === 0) {
      console.log('\n‚ö†Ô∏è No Service Staff users found. Please create a Service Staff user first.');
      process.exit(0);
    }

    // Find all unassigned tickets or tickets with status pending/in_progress
    const unassignedTickets = await SupportTicket.find({
      $or: [
        { assignedTo: { $exists: false } },
        { assignedTo: null }
      ],
      status: { $in: ['pending', 'in_progress'] }
    });

    console.log(`\nüé´ Found ${unassignedTickets.length} unassigned tickets with pending/in_progress status`);

    if (unassignedTickets.length === 0) {
      console.log('\n‚úÖ No unassigned tickets found. All tickets are already assigned.');

      // Show current ticket assignments
      const allTickets = await SupportTicket.find().populate('assignedTo', 'name email role');
      console.log(`\nüìä Current ticket assignments (${allTickets.length} total tickets):`);
      allTickets.forEach(ticket => {
        console.log(`  - Ticket #${ticket.ticketNumber}: ${ticket.subject}`);
        console.log(`    Status: ${ticket.status}, Priority: ${ticket.priority}`);
        console.log(`    Assigned to: ${ticket.assignedTo ? `${ticket.assignedTo.name} (${ticket.assignedTo.email})` : 'Unassigned'}`);
      });

      process.exit(0);
    }

    // Assign tickets to first Service Staff user (round-robin can be added later)
    const firstServiceStaff = serviceStaffUsers[0];
    console.log(`\n‚úèÔ∏è Assigning ${unassignedTickets.length} ticket(s) to ${firstServiceStaff.name}...`);

    for (const ticket of unassignedTickets) {
      await SupportTicket.findByIdAndUpdate(ticket._id, {
        assignedTo: firstServiceStaff._id
      });
      console.log(`  ‚úÖ Assigned Ticket #${ticket.ticketNumber} - "${ticket.subject}"`);
    }

    console.log(`\n‚ú® Successfully assigned ${unassignedTickets.length} ticket(s) to ${firstServiceStaff.name}`);
    console.log('\nüìä Now the Service Staff dashboard will show these tickets!');

  } catch (error) {
    console.error('‚ùå Error:', error);
    process.exit(1);
  } finally {
    await mongoose.connection.close();
    console.log('\nüîå Disconnected from MongoDB');
    process.exit(0);
  }
}

assignTicketsToServiceStaff();

