        .filter(([_, mongoId]) => foundDbProductIds.has(mongoId))
        .map(([originalId, _]) => originalId);

      const predefinedPartIds = vehicle.selectedParts.filter((id: string) => 
        !foundDbProductOriginalIds.includes(id)
